import './App.css';
import * as React from 'react';
import Massage from './massage.jsx';
import { useState, useRef, useEffect } from 'react';
import MessageList from './MessageList';
import { animateScroll as scroll } from "react-scroll";
import { TextField } from '@mui/material';
import ChatList from './Chats.jsx';
import MessageForm from './MessageForm.jsx';


function App() {
  const [message, setMessage] = useState('');
  const [messageList, setMesssageList] = useState([]);

  const ref = useRef(undefined);
  const user = "Michael";

  const createComment = (e) => setMessage(e.target.value);

  const addComment = () => {
    setMesssageList([...messageList, {text:message, user:user}]);
    setMessage('');
    scroll.scrollToBottom("smooth");
  };

  const answerBot = () => {
    setTimeout(
      () => 
      setMesssageList([
        ...messageList,
        {text:"hello i'm Bot. What is your name?", user:"RoBot" },
      ]),
      1500
    );
  };

  const scrollToBottom = () => {
    setTimeout(() =>
      scroll.scrollToBottom("smooth"),
      1500
    )
  }

  useEffect(() => {
    if (messageList[messageList.length - 1]?.user === user) {
      answerBot();
      scrollToBottom();
      return () => clearTimeout(answerBot, scrollToBottom);
    }
  }, );
  return (
    <div className="App">
      <header className="App-header">
        <MessageForm/>
      </header>
      <div className ="container">
        <ChatList/>
        <MessageList messageList={messageList} user={user}/>
      </div>

    </div>
  );
}
export default App;
