import './App.css';
import Massage from './massage.jsx';
import { useState } from 'react';
import { useRef } from 'react'; 
import { useEffect } from 'react';
import MessageList from './MessageList';

const comment = "Hello, dear guest!!!";

export const App = () => {
  const [text, setText] = useState('');
  const [messageList] = useState(["1","2"]);

  const ref = useRef(undefined);

  useEffect(() => {
    ref.current?.focus();
    }, []);
  const CreateComment = (e) => setText(e.target.value);

  return (
    <div className="App">

      <header className="App-header">
        <Massage comment = {comment}/>
        <textarea inputRef = {ref} value = {text} onChange={CreateComment}/>
        <button>Comment</button>
        <MessageList messageList={messageList}/>
      </header>
    </div>
  );
}

export default App;
