import './MessageForm.css';
import Massage from './massage.jsx';
import { TextField } from '@mui/material';
import { animateScroll as scroll } from "react-scroll";
import { useState, useRef, useEffect } from 'react';

const MessageForm = (props) => {
    const [message, setMessage] = useState('');
    const [messageList, setMesssageList] = useState([]);
    const ref = useRef(undefined);
    const user = "Michael";
    const createComment = (e) => setMessage(e.target.value);

    const addComment = () => {
      setMesssageList([...messageList, {text:message, user:user}]);
      setMessage('');
      scroll.scrollToBottom("smooth");
    };
    return (
        <div className = "container">
            <Massage user = {user}/>
            <form className = "commentForm" action="#">
            <TextField 
            inputref = {ref} 
            value = {message} 
            onChange={createComment}
            label={user}
            variant="outlined"
            fullWidth
            focused
            />
            <button className = "commentBtn" onClick={addComment}>Send</button>
            </form>
        </div>
    );
}

export default MessageForm;
