import './App.css';
import Massage from './massage.jsx';
import { useState } from 'react';
import { useRef } from 'react'; 
import { useEffect } from 'react';
import MessageList from './MessageList';



function App() {
  const [message, setMessage] = useState('');
  const [messageList, setMesssageList] = useState([]);
  const ref = useRef(undefined);
  const user = "Michael";
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    this.messagesEndRef.scrollIntoView({behavior: "smooth"});
  }

  const createComment = (e) => setMessage(e.target.value);

  const addComment = () => {
    setMesssageList([...messageList, {text: message, user: user}]);
    setMessage('');
  };

  const answerBot = () => {
    setTimeout(
      () => 
      setMesssageList([
        ...messageList,
        {text:"hello i'm Bot. What is your name?", author:"RoBot" },
      ]),
      1500
    );
    scrollToBottom()
  };

  useEffect(() => {
    if (messageList[messageList.length - 1]?.user === user) {
      answerBot();
      return () => clearTimeout(answerBot);
    }
  }, );
  return (
    <div className="App">
      <header className="App-header">
        <Massage user = {user}/>
        <form class = "commentForm" action="#">
        <textarea 
          inputref = {ref} 
          value = {message} 
          onChange={createComment}
        />
        <button onClick={addComment}>Comment</button>
        </form>
      </header>
      <div class="comments">
        <MessageList messageList={messageList} user={user}></MessageList>
        <div ref={messagesEndRef}></div>
      </div>
    </div>
  );
}

export default App;
