import './App.css';
import Massage from './massage.jsx';
import { useState } from 'react';
import { useRef } from 'react'; 
import { useEffect } from 'react';
import MessageList from './MessageList';
import faker from 'faker';



const user = "Michael";

function App() {
  const [message, setMessage] = useState("");
  const [messageList, setMesssageList] = useState([]);
  const ref = useRef(undefined);

  const createComment = (e) => setMessage(e.target.value);

  const addComment = () => {
    setMesssageList([...messageList, {text: message, user: user, id: faker.datatype.uuid()}]);
    setMessage("");
  };

  const answerBot = () => {
    setTimeout(
      () => 
      setMesssageList([
        ...messageList,
        {text:"hello i'm Bot. What is your name?", author:"RoBot", id: faker.datatype.uuid() },
      ]),
      1500
    );
  };

  useEffect(() => {
    if (messageList[messageList.length - 1]?.author === user) {
      answerBot();
      return () => clearTimeout(answerBot);
    }
  }, );

  return (
    <div className="App">
      <header className="App-header">
        <Massage user = {user}/>
        <form class = "commentForm" action="#">
        <textarea 
          inputref = {ref} 
          value = {message} 
          onChange={createComment}/>
        <button onClick={addComment}>Comment</button>
        </form>
      </header>
      <div class="comments">
        <MessageList messageList={messageList} user={user}></MessageList>
      </div>
    </div>
  );
}

export default App;
