import './App.css';
import * as React from 'react';
import Massage from './massage.jsx';
import { useState, useRef, useEffect } from 'react';
import MessageList from './MessageList';
import { animateScroll as scroll } from "react-scroll";
import { TextField } from '@mui/material';
import ChatList from './Chats.jsx';


function App() {
  const [message, setMessage] = useState('');
  const [messageList, setMesssageList] = useState([]);

  const ref = useRef(undefined);
  const user = "Michael";

  const createComment = (e) => setMessage(e.target.value);

  const addComment = () => {
    setMesssageList([...messageList, {text:message, user:user}]);
    setMessage('');
    scroll.scrollToBottom("smooth");
  };

  const answerBot = () => {
    setTimeout(
      () => 
      setMesssageList([
        ...messageList,
        {text:"hello i'm Bot. What is your name?", user:"RoBot" },
      ]),
      1500
    );
  };

  const scrollToBottom = () => {
    setTimeout(() =>
      scroll.scrollToBottom("smooth"),
      1500
    )
  }

  useEffect(() => {
    if (messageList[messageList.length - 1]?.user === user) {
      answerBot();
      scrollToBottom();
      return () => clearTimeout(answerBot, scrollToBottom);
    }
  }, );
  return (
    <div className="App">
      <header className="App-header">
        <Massage user = {user}/>
        <form className = "commentForm" action="#">
        <TextField 
          inputref = {ref} 
          value = {message} 
          onChange={createComment}
          label={user}
          variant="outlined"
          fullWidth
          focused
        />
        <button className = "commentBtn" onClick={addComment}>Send</button>
        </form>
      </header>
      <div className ="container">
        <div className ="comments">
        <MessageList messageList={messageList} user={user}></MessageList>
      </div>
      </div>

    </div>
  );
}
export default App;
